		public static void stopMoonEvent()
		{
			if (pumpkinMoon)
			{
				pumpkinMoon = false;
				if (netMode != 1)
				{
					NPC.waveKills = 0f;
					NPC.waveCount = 0;
				}
			}
			if (snowMoon)
			{
				snowMoon = false;
				if (netMode != 1)
				{
					NPC.waveKills = 0f;
					NPC.waveCount = 0;
				}
			}
		}

		public static void startPumpkinMoon()
		{
			pumpkinMoon = true;
			snowMoon = false;
			bloodMoon = false;
			if (netMode != 1)
			{
				NPC.waveKills = 0f;
				NPC.waveCount = 1;
				string nvasionWaveText = Lang.GetInvasionWaveText(1, 305);
				if (netMode == 0)
				{
					NewText(nvasionWaveText, 175, 75);
				}
				else if (netMode == 2)
				{
					NetMessage.SendData(25, -1, -1, nvasionWaveText, 16, 175f, 75f, 255f);
				}
			}
		}

