			int day = now.Day;
			int month = now.Month;
			if (day >= 15 && month == 12)
			{
				xMas = true;
			}
			else
			{
				xMas = false;
			}
		}

		public static void checkHalloween()
		{
			DateTime now = DateTime.Now;
			int day = now.Day;
			int month = now.Month;
			if (day >= 20 && month == 10)
			{
				halloween = true;
			}
			else if (day <= 10 && month == 11)
			{
				halloween = true;
			}
			else
			{
				halloween = false;
			}
		}

