			if (Math.IEEERemainder(netPlayCounter, 900.0) == 0.0)
			{
				bool flag = true;
				int num = lastItemUpdate;
				int num2 = 0;
				while (flag)
				{
					num++;
					if (num >= 400)
					{
						num = 0;
					}
					num2++;
					if (!item[num].active || item[num].owner == 16)
