				break;
			case 262:
				if (life <= lifeMax / 2)
				{
					result = 12;
				}
				break;
			case 125:
				if (ai[0] >= 3f)
				{
					result = 20;
				}
				break;
			case 126:
				if (ai[0] >= 3f)
				{
					result = 21;
				}
				break;
			case 439:
				if (ai[0] == 5f && alpha == 255)
				{
					result = -1;
				}
				break;
			case 440:
				if (ai[0] != 5f || alpha == 255)
				{
					result = -1;
				}
				break;
			}
			return result;
		}

