			if (townNPC && Main.netMode != 1)
			{
				PortalHelper.TryGoingThroughPortals(this);
			}
		}

		public override string ToString()
		{
			return "name:" + name + ", active:" + active + ", whoAmI:" + whoAmI;
		}
	}
}
